import React,{ useContext } from 'react';
import Input from '../../UI/Input';
import classes from './MealItemForm.module.css';
import CartContext from '../../../store/cart-context';

const MealItemForm = (props) => {
  const totalAmount=0;
  const cartcntx =useContext(CartContext);
  console.log('initialized',cartcntx)
  const addItemToCart = (event) => {
    event.preventDefault();
    const amount=totalAmount;
    const quantity = parseInt(document.getElementById('amount_' + props.id).value);
    amount= amount+((quantity)*(props.price));
    cartcntx.addItem({ ...props.item, quantity: quantity ,amount:amount});
    console.log(props.item);
  };


  return (
    <form className={classes.form}>
      {console.log('inside render',cartcntx.items)}
      <Input
        label='Amount'
        input={{
          id: 'amount_' + props.id,
          type: 'number',
          min: '1',
          max: '5',
          step: '1',
          defaultValue: '1',
        }}
      />
      <button onClick={addItemToCart}>+ Add</button>
    </form>
  );
};

export default MealItemForm;
