import React,{ useContext } from 'react';
import Input from '../../UI/Input';
import classes from './MealItemForm.module.css';
import CartContext from '../../../store/cart-context';

const MealItemForm = (props) => {
  
  const cartcntx =useContext(CartContext);
  
  const combinedItems = [];
  for (const item of cartcntx.items) {
    const existingItemIndex = combinedItems.findIndex((combinedItem) => combinedItem.id === item.id);
    if (existingItemIndex !== -1) {
      combinedItems[existingItemIndex].quantity += item.quantity;
    } else {
      combinedItems.push({ ...item });
    }
  }
  
  const addItemToCart = (event) => {
    event.preventDefault();
  
    const quantity = parseInt(document.getElementById('amount_' + props.id).value);
    
    cartcntx.addItem({ ...props.item, quantity: quantity});
    console.log(props.item);
  };


  return (
    <form className={classes.form}>
      {console.log('inside render',cartcntx.items)}
      <Input
        label='Amount'
        input={{
          id: 'amount_' + props.id,
          type: 'number',
          min: '1',
          max: '5',
          step: '1',
          defaultValue: '1',
        }}
      />
      <button onClick={addItemToCart}>+ Add</button>
    </form>
  );
};

export default MealItemForm;
